INSERT INTO `courses` (`id`, `name`, `objective`, `learning`, `duration`, `eligibility`, `category_id`) VALUES
(1, 'PH.D.', '1. Acquire refined research abilities and specialized knowledge in a particular area of study.\r\n\r\n2. Enhance the existing knowledge base by conducting innovative research and scholarly investigation.\r\n\r\n3. Engage in critical analysis and evaluation of existing research and literature.\r\n\r\n4. Demonstrate the ability to formulate research questions, design research methodologies, and collect and analyze data.\r\n', '1. Exhibit a high level of proficiency and mastery in a particular academic discipline.\r\n\r\n2. Undertake independent research to expand the existing reservoir of knowledge.\r\n\r\n3. Develop critical thinking and analytical skills necessary for advanced academic inquiry.\r\n\r\n4. Effectively convey research discoveries through scholarly publications and presentations.', '2', '55% or an equivalent grade (5% relaxation as per reservation policy)', 6),
(2, 'B.A(LLB)', '1. Establish a solid base in disciplines encompassing liberal arts, including humanities, social sciences, and languages.\r\n\r\n2. Acquire a comprehensive understanding of the basic principles and concepts of law.\r\n\r\n3. Gain proficiency in legal research methodologies while fostering analytical and critical thinking abilities.\r\n\r\n4. Comprehend the framework and operations of legal systems and institutions.\r\n\r\n5. Cultivate adeptness in written and verbal communication within legal contexts.\"\r\n', '1. Develop a comprehensive understanding of legal principles and concepts.\r\n\r\n2. Utilize legal reasoning and analysis to tackle intricate legal challenges.\r\n\r\n3. Exhibit adeptness in written and verbal communication within legal settings.\r\n\r\n4. Cultivate a sense of ethical consciousness and uphold professional responsibility in the field of law.\r\n', '5', '10+2 in any stream', 4),
(3, 'LLB\r\n', '1. Acquire a comprehensive understanding of the basic principles, concepts, and areas of law.\r\n\r\n2. Develop analytical and critical thinking skills for legal analysis and problem-solving.\r\n\r\n3. Understand the structure and functioning of the legal system and institutions.\r\n\r\n4. Develop effective communication skills for legal writing and oral advocacy.\r\n\r\n5. Analyze and interpret statutes, cases, and legal documents.\r\n\r\n6. Apply legal principles to real-world legal issues and scenarios.\r\n', '1. Acquire a foundational understanding of various branches of law.\r\n\r\n2. Interpret and apply legal rules and principles to practical scenarios.\r\n\r\n3. Demonstrate proficiency in legal research, writing, and analysis.\r\n\r\n4. Develop critical thinking and problem-solving skills relevant to the field of law.\r\n', '3', 'Graduation in any stream or equivalent.', 4),
(4, 'LLM', '1. Develop specialized knowledge in a specific area of law through advanced coursework and research.\r\n\r\n2. Assess and critically analyze legal theories, concepts, and principles relevant to the selected area of specialization.\r\n\r\n3. Undertake independent research and produce an exceptional research paper or thesis.\r\n\r\n4. Understand and apply advanced legal research methodologies and techniques.\r\n\r\n5. Develop expertise in legal writing and oral presentation skills.', '1. Gain specialized knowledge in a specific area of law.\r\n\r\n2. Analyze complex legal issues and propose innovative solutions.\r\n\r\n3. Conduct independent research and produce scholarly legal writing.\r\n\r\n4. Demonstrate advanced skills in legal advocacy and oral argumentation.', '2', 'Graduation in any stream or equivalent.', 4),
(5, 'D.Pharm', '1. Acquire a comprehensive understanding of pharmaceutical sciences, including drug formulation, manufacturing, and quality control.\r\n\r\n2. Gain expertise in pharmacology, pharmacokinetics, and the study of drug interactions.\r\n\r\n3. Acquire the ability to interpret prescriptions, dispense medications, and deliver optimal pharmaceutical care.\r\n\r\n4. Understand the legal and ethical aspects of pharmacy practice.\r\n\r\n5. Develop effective communication and counseling skills to educate patients about medication usage and safety', '1. Acquire a comprehensive knowledge of pharmaceutical sciences and drug development.\r\n\r\n2. Demonstrate competency in dispensing and managing medications.\r\n\r\n3. Apply principles of pharmacology and therapeutics to patient care.\r\n\r\n4. Develop skills in pharmaceutical analysis and quality control.\r\n', '2', 'Secondary (10+2) in PCM/PCB with minimum 50% marks.', 7),
(6, 'B.Pharm', '1. Develop a comprehensive understanding of pharmaceutical sciences, including drug formulation, manufacturing, and quality assurance.\r\n\r\n2. Gain in-depth knowledge of pharmacology, pharmacokinetics, and therapeutic principles.\r\n\r\n3. Acquire skills in drug analysis, quality control, and regulatory compliance.\r\n\r\n4. Understand the principles of pharmacy management and the healthcare system.\r\n\r\n5. Develop effective communication and counseling skills to interact with patients, healthcare professionals, and other stakeholders.\r\n', '1. Develop a strong foundation in pharmaceutical sciences and drug formulation.\r\n\r\n2. Demonstrate proficiency in compounding and dispensing medications.\r\n\r\n3. Apply principles of pharmacology and pharmacokinetics to patient care.\r\n\r\n4. Understand pharmaceutical regulations and ethical considerations in the field.\r\n', '4', 'Secondary (10+2) in PCM/PCB with minimum 50% marks.', 7),
(7, 'DMLT', '1. Acquire a comprehensive understanding of laboratory techniques, equipment, and procedures.\r\n\r\n2. Develop skills in specimen collection, handling, and processing.\r\n\r\n3. Learn to perform various laboratory tests, including clinical chemistry, hematology, microbiology, and immunology.\r\n\r\n4. Understand the principles of quality control and quality assurance in laboratory testing.\r\n\r\n5. Gain knowledge of laboratory safety protocols and infection control practices.\r\n\r\n6. Interpret and report laboratory test results accurately and efficiently.', '1. Acquire knowledge and skills in laboratory techniques and procedures.\r\n\r\n2. Perform accurate and reliable diagnostic tests in a clinical laboratory setting.\r\n\r\n3. Demonstrate proficiency in specimen collection, handling, and storage.\r\n\r\n4. Understand and adhere to laboratory safety protocols and quality assurance measures.', '2', 'Secondary (10+2) in PCM/PCB with minimum 50% marks.', 2),
(8, 'Pre-DMLT', '1. Provide foundational knowledge of medical laboratory technology.\r\n\r\n2. Introduce students to basic laboratory techniques, equipment, and procedures.\r\n\r\n3. Develop skills in specimen collection, handling, and processing.\r\n\r\n4. Familiarize students with common laboratory tests and their significance.\r\n\r\n5. Promote awareness of laboratory safety and infection control practices.', '1. Gain foundational knowledge of laboratory sciences and medical terminology.\r\n\r\n2. Assist in basic laboratory procedures and specimen handling.\r\n\r\n3. Demonstrate awareness of laboratory safety protocols.\r\n\r\n4. Develop skills in data entry and record keeping.', '3', 'Secondary (10+2) in PCM/PCB with minimum 50% marks.', 2),
(9, 'Bsc. MLT', '1. Acquire a comprehensive understanding of medical laboratory sciences, including laboratory techniques, instrumentation, and procedures.\r\n\r\n2. Develop proficiency in performing and interpreting various laboratory tests across different disciplines, including clinical chemistry, hematology, microbiology, and immunology.\r\n\r\n3. Gain knowledge of quality control and quality assurance practices in the laboratory.\r\n\r\n4. Understand the principles of laboratory management, including inventory control and workflow optimization.', '1. Develop a strong understanding of medical laboratory sciences.\r\n\r\n2. Perform a wide range of laboratory tests and analyses.\r\n\r\n3. Interpret and report laboratory findings accurately.\r\n\r\n4. Apply quality control measures and adhere to ethical guidelines in laboratory practice.', '3.6', 'Secondary (10+2) in PCM/PCB with minimum 50% marks.', 2),
(10, 'M.Sc MLT', '1. Develop advanced knowledge and expertise in specialized areas of medical laboratory technology.\r\n\r\n2. Conduct independent research and contribute to the advancement of knowledge in medical laboratory science.\r\n\r\n3. Gain proficiency in advanced laboratory techniques and instrumentation.\r\n\r\n4. Analyze and interpret complex.', '1. Acquire advanced knowledge and expertise in specialized areas of medical laboratory sciences.\r\n\r\n2. Conduct independent research and contribute to scientific advancements in the field.\r\n\r\n3. Demonstrate proficiency in advanced laboratory techniques and technologies.\r\n\r\n4. Communicate research findings effectively through scholarly publications and presentations.', '2', 'Must have a B.Sc degree in MLT or BMLT or an equivalent course', 2),
(11, 'BPT', '1. To provide students with a comprehensive understanding of human anatomy, physiology, and biomechanics.\r\n\r\n2. To develop proficiency in assessing, diagnosing, and treating various musculoskeletal, neurological, cardiovascular, and respiratory conditions through physiotherapy interventions.\r\n\r\n3. To equip students with the knowledge and skills required to design and implement therapeutic exercise programs, electrotherapy techniques, manual therapy, and other modalities.\r\n\r\n4. To foster critical thinking and problem-solving abilities in physiotherapeutic practice, including patient examination, evaluation, and treatment planning.\r\n\r\n5. To develop effective communication skills, ethical values, and professionalism in dealing with patients, healthcare professionals, and the community.', '1. Acquire an in-depth understanding of human anatomy, physiology, and biomechanics.\r\n\r\n2. Apply evidence-based principles to evaluate, strategize, and execute physical therapy interventions.\r\n\r\n3. Exhibit expertise in therapeutic exercises, manual therapy techniques, and the utilization of electrotherapy.\r\n\r\n4. Cultivate strong communication and patient management abilities within a clinical environment.', '4.6', '10+2 with a minimum of 50 percent marks from a recognized institute.', 2),
(12, 'MPT', '1. To provide advanced knowledge and skills in specialized areas of physiotherapy, such as musculoskeletal, neurology, cardiorespiratory, sports, or community physiotherapy.\r\n\r\n2. To enhance clinical reasoning and diagnostic abilities to enable accurate assessment and management of complex and challenging cases.\r\n\r\n3. To develop expertise in designing and implementing specialized physiotherapy interventions, including advanced therapeutic exercise, manual therapy techniques, and specialized modalities.\r\n\r\n4. To promote research and evidence-based practice in physiotherapy, enabling students to critically evaluate scientific literature and contribute to the advancement of the field.\r\n', '1. Acquire specialized knowledge and advanced skills in a specific area of physical therapy.\r\n\r\n2. Conduct research and contribute to evidence-based practice in physical therapy.\r\n\r\n3. Develop expertise in advanced therapeutic techniques and modalities.\r\n\r\n4. Demonstrate leadership and critical thinking skills in the field of physical therapy.', '2', 'Candidates must have a Bachelor of Physiotherapy degree from a recognized university with at least 50% grades and a 6-month internship', 2),
(13, 'BA(English)', '1. Develop an extensive understanding of the English language, encompassing its grammar and vocabulary.\r\n\r\n2. Strengthen critical reading and analytical abilities by engaging with literary works, including poetry, prose, and drama.\r\n\r\n3. Foster proficient written and verbal communication skills in the English language.\r\n\r\n4. Attain knowledge of diverse literary movements, periods, and genres.\r\n\r\n5. Explore cultural and historical contexts of literature in English.\r\n', '1. Develop critical reading and analytical skills to interpret and evaluate literary texts.\r\n\r\n2. Demonstrate effective written and oral communication skills in English.\r\n\r\n3. Gain knowledge of literary and cultural theories and apply them to literary analysis.\r\n\r\n4. Acquire a broad understanding of the historical development of English literature.', '3', '10+2 in any stream', 3),
(14, 'MA(English)', '1. Acquire profound expertise and critical comprehension of English literature and literary theories.\r\n\r\n2. Conduct an in-depth analysis of literary texts from different periods, genres, and cultural contexts.\r\n\r\n3. Participate in research endeavors and generate scholarly contributions within the realm of English literature.\r\n\r\n4. Strengthen critical thinking, analytical reasoning, and interpretive abilities.\r\n\r\n5. Foster advanced writing proficiency tailored for academic and professional pursuits.', '1. Develop advanced critical thinking and research skills in the field of English literature.\r\n\r\n2. Conduct independent research and produce scholarly work in English literature.\r\n\r\n3. Demonstrate expertise in a specific area of English literature through in-depth analysis.\r\n\r\n4. Engage in critical discussions and contribute to scholarly debates in the field.', '2', 'Graduation in any stream or equivalent', 3),
(15, 'BA(Economics)', '1. Develop a strong foundation in economic principles, theories, and concepts.\r\n\r\n2. Understand the dynamics of microeconomics and macroeconomics.\r\n\r\n3. Analyze and interpret economic data and indicators.\r\n\r\n4. Apply economic models and tools to analyze real-world economic issues.\r\n\r\n5. Develop critical thinking and problem-solving skills for economic analysis.', '1. Understand foundational concepts and theories in economics.\r\n\r\n2. Apply economic analysis to real-world issues and problems.\r\n\r\n3. Analyze and interpret economic data and statistics.\r\n\r\n4. Develop quantitative and analytical skills relevant to economics.', '3', '10+2 in any stream', 3),
(16, 'MA(Economics)', '1. Acquire advanced knowledge and expertise in economic theory and analysis.\r\n\r\n2. Conduct independent research and contribute to the field of economics.\r\n\r\n3. Apply advanced economic models and methodologies to analyze complex economic phenomena.\r\n\r\n4. Evaluate economic policies and their impact on society and markets.\r\n\r\n5. Engage with current debates and research in specialized areas of economics.', '1. Gain advanced knowledge of economic theories, models, and methodologies.\r\n\r\n2. Conduct independent research and contribute to economic knowledge.\r\n\r\n3. Analyze complex economic issues and propose policy recommendations.\r\n\r\n4. Demonstrate proficiency in econometric and statistical analysis.', '2', 'Graduation in any stream or equivalent', 3),
(17, 'BA(Sociology)', '1. Develop a sociological outlook and grasp of society and its intricacies of social behavior.\r\n\r\n2. Scrutinize social structures, institutions, and processes through analytical examination.\r\n\r\n3. Investigate sociological theories and concepts to comprehend and explain social phenomena.\r\n\r\n4. Examine social inequality, stratification, and the dynamics that shape society.\r\n\r\n5. Analyze prominent social issues including race, gender, class, and the impact of globalization.\r\n\r\n6. Engage in research endeavors, collecting and analyzing social data to inform sociological inquiry.', '1. Develop a comprehensive understanding of sociological theories and concepts.\r\n\r\n2. Apply sociological perspectives to analyze social phenomena and institutions.\r\n\r\n3. Conduct research using qualitative and quantitative sociological methods.\r\n\r\n4. Gain awareness of social inequalities, diversity, and social change.', '3', '10+2 in any stream', 3),
(18, 'MA(Sociology)', '1. Acquire advanced knowledge and expertise in sociological theories and research methods.\r\n\r\n2. Conduct independent research and contribute to the field of sociology.\r\n\r\n3. Engage with contemporary sociological debates and developments.\r\n\r\n4. Analyze complex social phenomena using advanced sociological theories and concepts.\r\n\r\n5. Apply advanced research methodologies to collect and analyze social data.', '1. Acquire advanced knowledge of sociological theories and debates.\r\n\r\n2. Conduct original research and contribute to sociological scholarship.\r\n\r\n3. Analyze complex social issues and propose solutions informed by sociological perspectives.\r\n\r\n4. Engage in critical discussions and debates on contemporary sociological issues.', '2', 'Graduation in any stream or equivalent', 3),
(19, 'BA(History)', '1. Foster a profound comprehension of historical events, movements, and civilizations.\r\n\r\n2. Examine primary and secondary historical sources and conduct critical assessments of their importance.\r\n\r\n3. Delve into various historical methodologies and approaches to expand perspective.\r\n\r\n4. Acquire knowledge about diverse historical periods and regions.\r\n\r\n5. Develop research and analytical skills specifically tailored for historical inquiry.', '1. Develop a comprehensive understanding of historical events, periods, and civilizations.\r\n\r\n2. Analyze primary and secondary sources critically to interpret historical information.\r\n\r\n3. Demonstrate effective research, analytical, and writing skills in historical studies.\r\n\r\n4. Gain awareness of historical methodologies and debates.', '3', '10+2 in any stream', 3),
(20, 'MA(History)', '1. Develop advanced knowledge and expertise in specialized areas of historical research.\r\n\r\n2. Conduct independent research and contribute to the field of history.\r\n\r\n3. Analyze and interpret complex historical sources and texts.\r\n\r\n4. Apply advanced historical methodologies and theories.\r\n\r\n5. Engage with current debates and research in specialized areas of history.\r\n\r\n6. Develop critical thinking and analytical skills for historical analysis.', '1. Acquire advanced knowledge of specialized areas in historical research.\r\n\r\n2. Conduct original research and contribute to historical scholarship.\r\n\r\n3. Analyze complex historical phenomena and interpret primary sources critically.\r\n\r\n4. Engage in critical debates and discussions on historical theories and methodologies.', '2', 'Graduation in any stream or equivalent', 3),
(21, 'BA in Political Science', '1. Develop a comprehensive understanding of political systems, theories, and concepts.\r\n\r\n2. Conduct an analysis of political institutions, processes, and ideologies.\r\n\r\n3. Engage in the study of comparative politics and international relations.\r\n\r\n4. Examine the intricate interplay between politics and society.\r\n\r\n5. Evaluate public policies and assess their consequential impact.\r\n', '1. Understand key concepts, theories, and debates in political science.\r\n\r\n2. Analyze political institutions, processes, and systems at local, national, and international levels.\r\n\r\n3. Evaluate and interpret political ideologies and policy frameworks.\r\n\r\n4. Develop critical thinking and communication skills relevant to political science.', '3', '10+2 in any stream', 3),
(22, 'MA in Political Science', '1. Acquire advanced knowledge and expertise in political science theories, research, and analysis.\r\n\r\n2. Conduct independent research and contribute to the field of political science.\r\n\r\n3. Analyze complex political phenomena and theories.\r\n\r\n4. Engage with contemporary debates and developments in political science.\r\n\r\n5. Apply advanced research methodologies to study political processes and institutions.\r\n', '1. Gain advanced knowledge of political theories, ideologies, and frameworks.\r\n\r\n2. Conduct original research and contribute to political science scholarship.\r\n\r\n3. Analyze complex political phenomena and propose policy recommendations.\r\n\r\n4. Engage in critical debates and discussions on contemporary political issues.', '2', 'Graduation in any stream or equivalent', 3),
(23, 'BSW', '1. Develop a comprehensive understanding of social work theories, values, and ethics.\r\n\r\n2. Conduct an analysis of social issues and assess their ramifications on individuals and communities.\r\n\r\n3. Grasp the principles of social justice, human rights, and empowerment.\r\n\r\n4. Hone abilities in evaluating and intervening in social problems and challenges.\r\n\r\n5. Participate in community development initiatives and engage in advocacy efforts.', '1. Develop a comprehensive understanding of social work principles, theories, and ethics.\r\n\r\n2. Apply social work skills and techniques to assess and intervene in individual and community issues.\r\n\r\n3. Advocate for social justice and promote equality and inclusion.\r\n\r\n4. Engage in reflective practice and professional development as a social worker.', '3', '10+2 in any stream', 3),
(24, 'MSW', '1. Acquire advanced knowledge and expertise in specialized areas of social work practice and research.\r\n\r\n2. Analyze and critically evaluate social work theories and interventions.\r\n\r\n3. Conduct independent research and contribute to the field of social work.\r\n\r\n4. Apply advanced social work methods and techniques in practice.\r\n\r\n5. Engage with contemporary debates and developments in social work.', '1. Develop advanced knowledge of social work theories, models, and interventions.\r\n\r\n2. Apply critical thinking and ethical decision-making skills in social work practice.\r\n\r\n3. Demonstrate cultural competence and sensitivity in working with diverse populations.\r\n\r\n4. Conduct comprehensive assessments and develop intervention plans to address individual, family, and community needs.\r\n\r\n5. Engage in advanced practice skills, such as counseling, advocacy, and case management.\r\n\r\n6. Apply research methods and evidence-based practices to inform social work interventions and policies.', '2', 'Graduation in any stream or equivalent', 3),
(25, 'BA in Rural Development', '1. Develop a comprehensive understanding of rural development theories, concepts, and practices.\r\n\r\n2. Analyze the socio-economic, political, and cultural dimensions of rural communities.\r\n\r\n3. Understand the challenges and opportunities in rural development.\r\n\r\n4. Develop knowledge of rural livelihood systems, agriculture, and natural resource management.\r\n\r\n5. Study rural governance, policies, and institutions.\r\n\r\n6. Acquire skills in community development, project management, and participatory approaches.', '1. Understand the key concepts and theories of rural development.\r\n\r\n2. Analyze the socio-economic and environmental factors influencing rural communities.\r\n\r\n3. Design and implement development projects and programs for rural areas.\r\n\r\n4. Promote sustainable and inclusive rural development practices.', '3', '10+2 in any stream', 3),
(26, 'MA in Rural Development', '1. Acquire advanced knowledge and expertise in specialized areas of rural development.\r\n\r\n2. Analyze and evaluate rural development policies, programs, and strategies.\r\n\r\n3. Conduct independent research and contribute to the field of rural development.\r\n\r\n4. Apply advanced analytical and research methodologies to study rural development issues.\r\n\r\n5. Develop skills in project planning, implementation, and evaluation.\r\n\r\n6. Understand the role of stakeholders and partnerships in rural development.', '1. Gain advanced knowledge of theories and approaches to rural development.\r\n\r\n2. Conduct research and contribute to rural development policy and practice.\r\n\r\n3. Analyze and evaluate the impact of development interventions on rural communities.\r\n\r\n4. Develop strategies for sustainable and participatory rural development.', '2', 'Graduation in any stream or equivalent', 3),
(27, 'BA in Education', '1. Develop a comprehensive understanding of educational theories, principles, and practices.\r\n\r\n2. Analyze the historical, philosophical, and sociological foundations of education.\r\n\r\n3. Understand the processes of teaching and learning in diverse educational settings.\r\n\r\n4. Explore educational policies, systems, and institutions.\r\n\r\n5. Gain knowledge of curriculum development and instructional strategies.', '1. Understand the foundational theories, principles, and practices of education.\r\n\r\n2. Analyze educational systems, policies, and practices.\r\n\r\n3. Develop instructional and curriculum design skills.\r\n\r\n4. Apply pedagogical strategies to facilitate effective teaching and learning.', '3', '10+2 in any stream', 3),
(28, 'MA in Education', '1. Acquire advanced knowledge and expertise in specialized areas of education.\r\n\r\n2. Analyze and critically evaluate educational theories, policies, and practices.\r\n\r\n3. Conduct independent research and contribute to the field of education.\r\n\r\n4. Apply advanced research methodologies to study educational phenomena.\r\n\r\n5. Engage with current debates and developments in educational theory and practice.', '1. Gain advanced knowledge of educational theories, research, and methodologies.\r\n\r\n2. Conduct independent research and contribute to educational scholarship.\r\n\r\n3. Analyze and evaluate educational policies and practices.\r\n\r\n4. Develop leadership and advocacy skills in the field of education.', '2', 'Graduation in any stream or equivalent', 3),
(29, 'BCA', '1. Develop a strong foundation in computer science and programming principles.\r\n\r\n2. Gain practical skills in programming languages, software development, and database management.\r\n\r\n3. Understand the fundamentals of computer networks, operating systems, and web technologies.\r\n\r\n4. Apply problem-solving and analytical skills to develop software solutions.\r\n\r\n5. Acquire knowledge of computer hardware and system architecture.', '1. Develop a strong foundation in computer science and information technology.\r\n\r\n2. Acquire programming and software development skills.\r\n\r\n3. Design and develop computer-based solutions to real-world problems.\r\n\r\n4. Demonstrate proficiency in database management and system analysis.', '3', '10+2 Examination / Equivalent Examination with at least 50% marks in aggregate in 5 subjects including English as compulsory subject', 8),
(30, 'MCA', '1. Acquire advanced knowledge and expertise in specialized areas of computer applications.\r\n\r\n2. Develop proficiency in software development, database management, and systems analysis.\r\n\r\n3. Apply advanced programming languages and frameworks for software development.\r\n\r\n4. Gain expertise in network management, cybersecurity, and computer architecture.\r\n\r\n5. Analyze and design efficient algorithms and data structures.', '1. Acquire advanced knowledge and expertise in computer science and applications.\r\n\r\n2. Analyze and solve complex problems in software development and information systems.\r\n\r\n3. Design and manage large-scale software projects.\r\n\r\n4. Demonstrate leadership and management skills in the field of computer applications.', '2', 'Graduation in any stream or equivalent', 8),
(31, 'Diploma in Fire & Safety', '1. Develop a comprehensive understanding of fire and safety management principles.\r\n\r\n2. Acquire a solid understanding of the core concepts related to fire prevention, fire protection systems, and emergency response.\r\n\r\n3. Evaluate strategies for risk assessment and mitigation in the realm of fire safety.\r\n\r\n4. Acquire knowledge of the regulations, codes, and standards governing fire safety.', '1. Understand the principles of fire safety and hazard management.\r\n\r\n2. Apply fire prevention and control measures in various settings.\r\n\r\n3. Conduct risk assessments and develop emergency response plans.\r\n\r\n4. Demonstrate knowledge of relevant safety regulations and protocols.', '2', '10+2 in any stream', 8),
(32, 'B.sc in Fire Safety and Hazard Management', '1. Establish a solid grounding in the field of fire safety engineering and the management of hazards.\r\n\r\n2. Comprehend the fundamental principles of fire science, fire behavior, and methods of fire prevention.\r\n\r\n3. Evaluate the design considerations and functionality of fire protection systems.\r\n\r\n4. Acquire knowledge pertaining to hazardous materials, including their safe handling and proper storage.\r\n\r\n5. Delve into the study of emergency response planning and the effective management of incidents.', '1. Develop a comprehensive understanding of fire science, safety principles, and hazard management.\r\n\r\n2. Implement effective fire prevention and control strategies.\r\n\r\n3. Conduct risk assessments and develop emergency response plans for different environments.\r\n\r\n4. Apply relevant safety standards and regulations in fire safety practices.', '3', 'Students should have passed 12th with 50% marks or above', 8),
(33, 'B.com', '• Develop a strong foundation in accounting, finance, economics, and business management.\r\n\r\n• Grasp the principles and operational methodologies of commerce within the context of a global business environment.\r\n\r\n• Foster critical thinking and analytical abilities essential for making informed business decisions.\r\n\r\n• Gain proficiency in business laws, taxation regulations, and the intricacies of financial reporting.\r\n\r\n• Hone effective communication and interpersonal skills to excel in professional interactions.', '1. Acquire a strong foundation in accounting, finance, and business management.\r\n\r\n2. Apply principles of financial analysis and decision-making in business contexts.\r\n\r\n3. Understand and interpret financial statements and reports.\r\n\r\n4. Develop effective communication and analytical skills relevant to the field of commerce.', '3', '10+2 or equivalent', 1),
(34, 'M.com', '• Acquire advanced knowledge and expertise in specialized areas of commerce.\r\n\r\n• Conduct independent research and contribute to the field of commerce.\r\n\r\n• Analyze complex financial data and apply advanced financial management techniques.\r\n\r\n• Understand advanced concepts in accounting, taxation, and business law.\r\n\r\n• Engage with current debates and developments in commerce and business.', '1. Gain advanced knowledge in specialized areas of commerce and business management.\r\n\r\n2. Analyze and interpret complex financial data and reports.\r\n\r\n3. Conduct research and contribute to the advancement of knowledge in commerce.\r\n\r\n4. Apply advanced financial and managerial techniques in business decision-making.', '2', 'Graduation in any stream or equivalent', 1),
(35, 'BBA', '• Establish a solid grounding in the principles, theories, and practical applications of business management.\r\n\r\n• Comprehend the various functional domains of business, encompassing marketing, finance, human resources, and operations.\r\n\r\n• Develop proficiencies in leadership, teamwork, and effective communication.\r\n\r\n• Engage in critical analysis of business challenges and apply problem-solving techniques.\r\n\r\n• Acquire knowledge pertaining to business ethics, entrepreneurship, and fostering innovation.', '1. Develop a comprehensive understanding of business principles, theories, and practices.\r\n\r\n2. Utilize management and organizational concepts to navigate diverse business contexts.\r\n\r\n3. Employ critical thinking and decision-making skills to analyze and resolve business problems.\r\n\r\n4. Showcase effective communication and leadership competencies within a business setting.\r\n\r\n5. Implement ethical and sustainable business practices.\r\n\r\n6. Collaborate efficiently within team environments and exhibit strong interpersonal skills.\r\n\r\n7. Adapt to and thrive in dynamic business environments.', '3', '10+2 or equivalent', 5),
(36, 'MBA', '• Acquire advanced knowledge and expertise in various aspects of business management.\r\n\r\n• Develop leadership and strategic management skills for effective decision-making.\r\n\r\n• Analyze and evaluate business environments, market trends, and competitive landscapes.\r\n\r\n• Apply advanced business theories and models to solve complex problems.\r\n\r\n• Develop skills in business planning, project management, and organizational behavior.\r\n\r\n• Engage with current business issues and practices through case studies and real-world applications.', '1. Develop a high level of proficiency and specialized knowledge in the field of business administration.\r\n\r\n2. Conduct comprehensive analysis and assessment of business strategies and organizational practices.\r\n\r\n3. Demonstrate strong leadership and management competencies across various business environments.\r\n\r\n4. Apply critical thinking and problem-solving skills to address intricate business challenges.\r\n\r\n5. Cultivate effective decision-making abilities grounded in financial analysis and awareness of market trends.\r\n\r\n6. Demonstrate adept communication and negotiation skills within a global business context.\r\n\r\n7. Demonstrate an understanding of ethical, social, and environmental responsibilities in business.', '2', 'Graduation in any stream or equivalent', 5),
(37, 'BBA in Hotel Management', '• Develop a comprehensive understanding of the hospitality industry and its management principles.\r\n\r\n• Acquire knowledge encompassing multiple facets of hotel operations, ranging from front office management to food and beverage services, housekeeping, and event coordination.\r\n\r\n• Develop proficiencies in customer service, effective communication, and leadership within the context of a hotel environment.\r\n\r\n• Grasp the financial and marketing dimensions involved in the management of hotels.', '1. Develop a comprehensive understanding of the hospitality industry and hotel operations.\r\n\r\n2. Apply principles of hospitality management, including front office, housekeeping, and food and beverage.\r\n\r\n3. Demonstrate knowledge of hotel marketing, sales, and revenue management strategies.\r\n\r\n4. Understand the importance of customer service and guest satisfaction in the hotel industry.', '3', '10+2 from a recognized board with 55% aggregate marks ', 5),
(38, 'Advance Dip in Hotel Management', '• Foster a high level of expertise and proficiency in diverse facets of hotel management.\r\n\r\n• Comprehend the underlying principles and operational methodologies of hotel operations, spanning front office management, food and beverage services, housekeeping, and event coordination.\r\n\r\n• Acquire and refine leadership, teamwork, and communication skills applicable within the dynamic environment of a hotel.\r\n\r\n• Engage in strategic analysis and implementation of management techniques tailored for the hospitality industry.\r\n\r\n• Develop knowledge pertaining to revenue management, customer relationship management, and quality assurance within the context of hotels.', '1. Develop specialized knowledge and skills in hotel management.\r\n\r\n2. Apply principles of front office management, housekeeping, and food and beverage operations.\r\n\r\n3. Demonstrate knowledge of hotel marketing and sales techniques.\r\n\r\n4. Apply financial management and cost control practices in a hotel setting.\r\n\r\n5. Understand the importance of customer service and guest satisfaction.', '2', 'Candidates should have completed the 10+2 or its equivalent exam in the Science stream with Hotel Management and Catering as one of the subjects.', 5),
(39, 'Diploma in Hotel Management', '• Establish a robust understanding of the fundamental principles underpinning hotel management.\r\n\r\n• Comprehend the operational dynamics within key hotel departments, encompassing front office, food and beverage, housekeeping, and event management.\r\n\r\n• Acquire comprehensive knowledge pertaining to customer service, ethical practices in hospitality, and cultural awareness.\r\n\r\n• Foster the development of essential management and leadership skills suitable for entry-level positions within the dynamic hospitality industry.\r\n\r\n• Gain valuable practical experience through hands-on training and exposure to real-world industry practices.', '1. Gain foundational knowledge of hotel management principles and practices.\r\n\r\n2. Understand the various departments and operations within a hotel.\r\n\r\n3. Apply basic principles of front office management and housekeeping.\r\n\r\n4. Demonstrate knowledge of food and beverage operations in a hotel setting.\r\n\r\n5. Develop customer service skills and interpersonal communication abilities.', '1', '10+2 in any stream', 5),
(40, 'Bachelor in Hotel Management', '• Develop an extensive understanding of the hospitality industry and its various elements.\r\n\r\n• Acquire a comprehensive knowledge of hotel operations, including front office management, food and beverage services, housekeeping, and event coordination.\r\n\r\n• Cultivate skills in leadership, teamwork, and effective communication within the context of a hotel setting.\r\n\r\n• Gain insight into marketing strategies, financial management, and strategic planning within the dynamic hospitality industry.', '1. Develop a comprehensive understanding of hotel management theories and practices.\r\n\r\n2. Apply principles of front office management, housekeeping, and food and beverage operations.\r\n\r\n3. Demonstrate knowledge of hotel marketing, sales, and revenue management strategies.\r\n\r\n4. Apply financial management and cost control practices in a hotel setting.\r\n', '3', '10+2 Examination / Equivalent Examination with at least 50% marks in aggregate in 5 subjects including English as a compulsory subject', 5),
(41, 'Master in Hotel Management', '• Acquire advanced knowledge and expertise in hotel management theory and practice.\r\n\r\n• Analyze and evaluate current trends and developments in the global hospitality industry.\r\n\r\n• Develop strategic management skills for decision-making in hotel operations.\r\n\r\n• Conduct research and contribute to the field of hotel management.\r\n\r\n• Understand advanced concepts in revenue management, service quality, and sustainable practices in the hospitality industry.', '1. Acquire advanced knowledge and expertise in hotel management.\r\n\r\n2. Analyze and evaluate hotel operations and strategies.\r\n\r\n3. Develop innovative approaches to hotel marketing, revenue management, and customer service.\r\n\r\n4. Apply financial analysis and strategic planning in a hotel setting.\r\n\r\n5. Demonstrate leadership and decision-making skills specific to the hotel industry.', '2', 'Graduation in any stream or equivalent', 5);
